SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_tipo_processo_adep ( nr_seq_processo_p bigint) RETURNS varchar AS $body$
DECLARE

				
nr_seq_solucao_w		integer;
nr_seq_procedimento_w	adep_processo.nr_seq_procedimento%type;
ie_tipo_processo_w		varchar(1);
nr_seq_material_w		adep_processo.nr_seq_material%type;
ie_agrupador_w			prescr_material.ie_agrupador%type;
				

BEGIN
if (nr_seq_processo_p IS NOT NULL AND nr_seq_processo_p::text <> '') then

	select	coalesce(max(nr_seq_solucao),0),
			coalesce(max(nr_seq_procedimento),0),
			coalesce(max(nr_seq_material),0)
	into STRICT	nr_seq_solucao_w,
			nr_seq_procedimento_w,
			nr_seq_material_w
	from	adep_processo
	where	nr_sequencia = nr_seq_processo_p;
	
	if (nr_seq_solucao_w > 0) then
		ie_tipo_processo_w	:= 'S';
	elsif ((nr_seq_procedimento_w > 0) and (coalesce(nr_seq_material_w,0) = 0)) then -- aqui
		ie_tipo_processo_w	:= 'P';
	else
	
		select	max(ie_agrupador)
		into STRICT	ie_agrupador_w
		from	prescr_mat_hor
		where	nr_seq_processo = nr_seq_processo_p
		and		nr_seq_material = nr_seq_material_w;
		
		if (ie_agrupador_w = 11) then
			ie_tipo_processo_w	:= 'N';
		elsif (ie_agrupador_w = 8) then
			ie_tipo_processo_w	:= 'E';
		else
			ie_tipo_processo_w	:= 'M';		
		end if;
	
		
	end if;

end if;

return ie_tipo_processo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_tipo_processo_adep ( nr_seq_processo_p bigint) FROM PUBLIC;

