CREATE OR REPLACE FUNCTION obter_tipo_proc_3m (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_proc_princ_w			procedimento_pac_medico.ie_proc_princ%type;
ie_proc_adicional_w		procedimento_pac_medico.ie_proc_adicional%type;
ie_tipo_procedimento_w		varchar(5);


BEGIN

begin
select	ie_proc_princ,
	ie_proc_adicional
into STRICT	ie_proc_princ_w,
	ie_proc_adicional_w
from	procedimento_pac_medico
where	nr_seq_propaci	= nr_sequencia_p;
exception
when others then
	ie_proc_princ_w		:= 'N';
	ie_proc_adicional_w	:= 'N';
end;

if (ie_proc_princ_w = 'S') then
	ie_tipo_procedimento_w := 'HL';
elsif (ie_proc_adicional_w = 'S') then
	ie_tipo_procedimento_w := 'NL';
end if;

return	ie_tipo_procedimento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
