CREATE OR REPLACE FUNCTION obter_tipo_projeto_gpi ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_tipo_w		bigint;
ds_tipo_w		varchar(255);
ds_retorno_w		varchar(255);


BEGIN

if (coalesce(nr_sequencia_p,0) <> 0) then
	begin
	select	a.nr_sequencia,
		a.ds_tipo
	into STRICT	nr_seq_tipo_w,
		ds_tipo_w
	from	gpi_tipo_projeto a
	where	a.nr_sequencia = nr_sequencia_p;


	if (ie_opcao_p = 'C') then
		ds_retorno_w := nr_seq_tipo_w;
	elsif (ie_opcao_p = 'D') then
		ds_retorno_w := ds_tipo_w;
	end if;
	end;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
