CREATE OR REPLACE FUNCTION obter_tipo_registro_res ( ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


ds_tipo_w		varchar(255);


BEGIN
if (ie_tipo_p IS NOT NULL AND ie_tipo_p::text <> '')  then

	if ( ie_tipo_p = 'AL') then
		-- 	Histórico de saúde (Alergias e reações adversas)
		ds_tipo_w := obter_desc_expressao(703993,'Alergias e reações adversas');

	elsif ( ie_tipo_p = 'ME') then
		-- 	Histórico de saúde (Medicamentos em uso)
		ds_tipo_w := obter_desc_expressao(293088,'Medicamentos em uso');

	elsif ( ie_tipo_p = 'DI') then
		-- Problemas e diagnósticos
		ds_tipo_w := obter_desc_expressao(296352,'Problemas');

	elsif ( ie_tipo_p = 'IM') then
		-- Imunizações
		ds_tipo_w := obter_desc_expressao(742811,'Imunizações');

	elsif ( ie_tipo_p = 'PR') then
		--Procedimentos
		ds_tipo_w := obter_desc_expressao(296717,'Procedimentos');

	elsif ( ie_tipo_p = 'EL') then
		--Exames de laboratório
		ds_tipo_w := obter_desc_expressao(314486,'Exames de laboratório');

	elsif ( ie_tipo_p = 'EI') then
		--Exames de imagem
		ds_tipo_w := obter_desc_expressao(316680,'Exames de imagem');

	elsif ( ie_tipo_p = 'SVA') then
		 -- Sinais vitais (Antropometria)
		 ds_tipo_w := obter_desc_expressao(298520,'Sinais vitais');

	elsif ( ie_tipo_p = 'AT') then
		 -- Sinais vitais (Antropometria)
		 ds_tipo_w := obter_desc_expressao(700869,'Antropometria');

	elsif ( ie_tipo_p = 'SVP') then
		 -- Sinais vitais (Pressão arterial)
		 ds_tipo_w := obter_desc_expressao(298520,'Sinais vitais');

	elsif ( ie_tipo_p = 'SVC') then
		 -- Sinais vitais (Frequência cardíaca)
		ds_tipo_w := obter_desc_expressao(298520,'Sinais vitais');

    elsif ( ie_tipo_p = 'SVR') then
		 -- Sinais vitais (Frequência respiratória)
		 ds_tipo_w := obter_desc_expressao(298520,'Sinais vitais');

    elsif ( ie_tipo_p = 'HSA') then
		 -- Histórico de saúde (Antecedentes pessoais)
		 ds_tipo_w := obter_desc_expressao(283523,'Antecedentes pessoais');

	elsif ( ie_tipo_p = 'HSF') then
		 -- Histórico de saúde (Antecedentes familiares)
	ds_tipo_w := obter_desc_expressao(283522,'Antecedentes familiares');

	elsif ( ie_tipo_p = 'HSH') then
		 -- Histórico de saúde (Hábitos)
		 ds_tipo_w := obter_desc_expressao(291200,'Hábitos');

	elsif ( ie_tipo_p = 'SV') then
		 -- Sinais vitais
		 ds_tipo_w := obter_desc_expressao(298520,'Sinais vitais');

	end if;


end if;

return ds_tipo_w;

end;
