CREATE OR REPLACE FUNCTION obter_tipo_regra_plantao ( nr_seq_regra_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE

/*
C	- CÃ³digo;
*/
ds_retorno_w	varchar(255);
ie_tipo_regra_w	varchar(15);


BEGIN

select	max(ie_tipo_regra)
into STRICT	ie_tipo_regra_w
from	regra_producao_plantao
where	nr_sequencia	= nr_seq_regra_p;

if (ie_tipo_p	= 'C') then
	ds_retorno_w	:= ie_tipo_regra_w;
end if;

return ds_retorno_w;

end;
$body$
