CREATE OR REPLACE FUNCTION obter_tipo_servico_regulado ( nr_seq_regulacao_p regulacao_atend.nr_sequencia%TYPE, ie_opcao_p text default null) RETURNS varchar AS $body$
DECLARE


ds_retorno_w 	varchar(255);
ie_opcao_to_w	constant varchar(255) := 'TO';


BEGIN

if (nr_seq_regulacao_p IS NOT NULL AND nr_seq_regulacao_p::text <> '') then

	if ( ie_opcao_p = ie_opcao_to_w)then
		Select 	max(ie_tipo)
		into STRICT	ds_retorno_w
		from 	regulacao_atend
		where	nr_sequencia = nr_seq_regulacao_p;
	else
		select	substr(obter_valor_dominio(8800,max(ie_tipo)),1,255)
		into STRICT	ds_retorno_w
		from 	regulacao_atend
		where	nr_sequencia = nr_seq_regulacao_p;
		
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
