CREATE OR REPLACE FUNCTION obter_tipo_serv_homecare ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255) := 'N';
nr_seq_regulacao_w	bigint;

/*
Regulado				RE
Tipo de servi√ßo			TS
*/
BEGIN


if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	Select  max(nr_seq_regulacao)
	into STRICT	nr_seq_regulacao_w
	from	agenda_lista_espera
	where	nr_sequencia = nr_sequencia_p;
	
	if ( ie_opcao_p = 'RE') then
	
		If (nr_seq_regulacao_w IS NOT NULL AND nr_seq_regulacao_w::text <> '') then
		
			ds_retorno_w := 'S';
		
		end if;	
	
	elsif (  ie_opcao_p = 'TS' ) then
	
		ds_retorno_w := '';
	
		If (nr_seq_regulacao_w IS NOT NULL AND nr_seq_regulacao_w::text <> '') then
		
			Select  ie_tipo
			into STRICT	ds_retorno_w
			from 	regulacao_atend
			where	nr_sequencia = nr_seq_regulacao_w;
			

		else
		
			ds_retorno_w := '';
		
		end if;	
		
	elsif (  ie_opcao_p = 'TO' ) then
	
		ds_retorno_w := '';
	
		If (nr_seq_regulacao_w IS NOT NULL AND nr_seq_regulacao_w::text <> '') then
		
			Select 	max(ie_tipo)
			into STRICT	ds_retorno_w
			from 	regulacao_atend
			where	nr_sequencia = nr_seq_regulacao_w;
			

		else
		
			ds_retorno_w := '';
		
		end if;	
	
	
	
	
	end if;


end if;


return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
