SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_tipo_sist_corp_hum_desc ( nr_sequencia_p bigint, ie_tipo_return_p text) RETURNS varchar AS $body$
DECLARE


ds_return_w				varchar(255) := null;
ds_nome_item_w			varchar(255) := null;
ds_tipo_item_w			varchar(255) := null;
nr_seq_exame_w			sistema_corpo_humano_item.nr_seq_exame%type := null;
nr_seq_sinal_w			sistema_corpo_humano_item.nr_seq_sinal%type := null;
nr_seq_escala_doc_w		sistema_corpo_humano_item.nr_seq_escala_doc%type := null;


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	select	a.nr_seq_exame,
			a.nr_seq_sinal,
			a.nr_seq_escala_doc
	into STRICT	nr_seq_exame_w,
			nr_seq_sinal_w,
			nr_seq_escala_doc_w
	from	sistema_corpo_humano_item a
	where	a.nr_sequencia = nr_sequencia_p;

	if (nr_seq_exame_w IS NOT NULL AND nr_seq_exame_w::text <> '') then
		select	a.nm_exame ds
		into STRICT	ds_nome_item_w
		from	exame_laboratorio a
		where	a.nr_seq_exame = nr_seq_exame_w
		order by ds asc;

		ds_tipo_item_w := obter_desc_expressao(316990);

	elsif (nr_seq_sinal_w IS NOT NULL AND nr_seq_sinal_w::text <> '') then
		select	a.ds_sinal_vital ds
		into STRICT	ds_nome_item_w
		from 	sinal_vital a,
				pep_informacao b
		where	a.nm_atributo = b.nm_atributo_inf
		and		b.ie_grupo_suep = 'SV'
		and     b.ie_suep = 'S'
		and		a.nr_sequencia = nr_seq_sinal_w
		order by ds asc;

		ds_tipo_item_w := obter_desc_expressao(298525);

	elsif (nr_seq_escala_doc_w IS NOT NULL AND nr_seq_escala_doc_w::text <> '') then
		select	distinct a.ds_caption ds
		into STRICT	ds_nome_item_w
		from    escala_documentacao a,
				pep_informacao b
		where	a.ie_escala = b.ie_escala
		and		a.nr_sequencia = nr_seq_escala_doc_w
		order by ds asc;

		ds_tipo_item_w := obter_desc_expressao(314487);

	end if;

	if (ie_tipo_return_p = 'T') then
		ds_return_w := ds_tipo_item_w;

	elsif (ie_tipo_return_p = 'D') then
		ds_return_w := ds_nome_item_w;

	end if;

end if;

return ds_return_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
