CREATE OR REPLACE FUNCTION obter_tipo_status_aval_marca ( nr_seq_status_aval_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1) := 'X';
qt_existe_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	status_avaliacao_marca
where	nr_sequencia = nr_seq_status_aval_p;

if (qt_existe_w > 0) then
	select	coalesce(ie_tipo_status,'X') ie_tipo_status
	into STRICT	ie_retorno_w
	from	status_avaliacao_marca
	where	nr_sequencia = nr_seq_status_aval_p;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
