SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_tipo_titulo_padrao (cd_estabelecimento_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_tipo_titulo_pad_w	parametros_contas_pagar.ie_tipo_titulo_pad%type;
ds_valor_parametro_w	varchar(4000) := '';
ds_tipo_titulo_w	varchar(15) := '';
ie_contido_w		varchar(1);


BEGIN

ds_valor_parametro_w := obter_param_usuario(851, 164, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ds_valor_parametro_w);

select	max(ie_tipo_titulo_pad)
into STRICT	ie_tipo_titulo_pad_w
from	parametros_contas_pagar
where	cd_estabelecimento	= cd_estabelecimento_p;

if (ie_tipo_titulo_pad_w IS NOT NULL AND ie_tipo_titulo_pad_w::text <> '') and (coalesce(ds_valor_parametro_w::text, '') = '') then

	ds_tipo_titulo_w	:= ie_tipo_titulo_pad_w;

elsif (ds_valor_parametro_w IS NOT NULL AND ds_valor_parametro_w::text <> '') then

	select	obter_se_contido_entre_virgula(ds_valor_parametro_w,ie_tipo_titulo_pad_w)
	into STRICT	ie_contido_w
	;

	if (ie_contido_w = 'N') then

		select	min(somente_numero(a.vl_dominio))
		into STRICT	ds_tipo_titulo_w
		from	valor_dominio a
		where	',' || replace(trim(both ds_valor_parametro_w),' ',null) || ',' like '%,' || trim(both a.vl_dominio) || ',%'
		and	coalesce(a.ie_situacao,'A')	= 'A'
		and	a.cd_dominio	= 502;

	else

		ds_tipo_titulo_w	:= ie_tipo_titulo_pad_w;

	end if;
else

	ds_tipo_titulo_w	:= '1';

end if;

return	ds_tipo_titulo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
