CREATE OR REPLACE FUNCTION obter_tipo_tributacao_item ( cd_estabelecimento_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


cd_classe_material_w      	bigint;
cd_subgrupo_material_w    	bigint;
cd_grupo_material_w       	bigint;
ds_retorno_w			varchar(15)	:= 'N';

c01 CURSOR FOR
SELECT	coalesce(ie_tipo_tributo_item,'N')
from	tipo_tributacao_item
where	cd_estabelecimento	= cd_estabelecimento_p
and	coalesce(cd_material, coalesce(cd_material_p, 0)) 			= coalesce(cd_material_p, 0)
and	coalesce(cd_subgrupo_material, coalesce(cd_subgrupo_material_w, 0))	= coalesce(cd_subgrupo_material_w, 0)
and	coalesce(cd_grupo_material, coalesce(cd_grupo_material_w, 0))		= coalesce(cd_grupo_material_w, 0)
and	coalesce(cd_classe_material, coalesce(cd_classe_material_w, 0))  		= coalesce(cd_classe_material_w, 0)
order by 	coalesce(cd_material,0),
		coalesce(cd_classe_material,0),
		coalesce(cd_subgrupo_material,0),
		coalesce(cd_grupo_material,0);


BEGIN

begin
select	coalesce(max(cd_classe_material),0),
	coalesce(max(cd_subgrupo_material),0),
	coalesce(max(cd_grupo_material),0)
into STRICT	cd_classe_material_w,
	cd_subgrupo_material_w,
	cd_grupo_material_w
from	estrutura_material_v
where	cd_material	 	= cd_material_p;
exception when others then
	cd_classe_material_w 	:= 0;
	cd_subgrupo_material_w 	:= 0;
	cd_grupo_material_w 	:= 0;
end;

open C01;
loop
fetch C01 into
	ds_retorno_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_retorno_w	:= ds_retorno_w;
	end;
end loop;
close C01;


return ds_retorno_w;

end;
$body$
