CREATE OR REPLACE FUNCTION obter_tipo_ultimo_historico_os ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
-------------------------------------------------------------------------------------------------------------------
Referências:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
vl_retorno_w		varchar(80);
nr_sequencia_w		bigint;
nr_seq_tipo_w		bigint;


BEGIN
select 	max(nr_sequencia)
into STRICT	nr_sequencia_w
from	man_ordem_serv_tecnico
where	nr_seq_ordem_serv	= nr_sequencia_p;

if (coalesce(nr_sequencia_w,0) <> 0) then
	select	nr_seq_tipo
	into STRICT	nr_seq_tipo_w
	from	man_ordem_serv_tecnico
	where	nr_sequencia	= nr_sequencia_w;

	select 	substr(obter_desc_expressao(cd_exp_tipo, ds_tipo),1,80)
	into STRICT	vl_retorno_w
	from	man_tipo_hist
	where	nr_sequencia	= nr_seq_tipo_w;
end if;

return vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
