CREATE OR REPLACE FUNCTION obter_tiss_convenio_setor ( cd_convenio_p bigint, cd_estabelecimento_p bigint, cd_setor_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE


/*Retorna apenas úm único setor se houver mais de um registro*/

nr_sequencia_w	bigint;

c01 CURSOR FOR
	SELECT	nr_sequencia
	from	tiss_senha_conexao
	where	cd_convenio		= cd_convenio_p
	and	cd_estabelecimento	= cd_estabelecimento_p
	and 	coalesce(cd_setor_atendimento,coalesce(cd_setor_atendimento_p,0))  = coalesce(cd_setor_atendimento_p,0)
	and (ds_login_prest IS NOT NULL AND ds_login_prest::text <> '' AND ds_senha_prest_hash IS NOT NULL AND ds_senha_prest_hash::text <> '')
	order by coalesce(cd_setor_atendimento,0), coalesce(cd_convenio,0);


BEGIN


open c01;
loop
fetch c01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
end loop;
close c01;

return nr_sequencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
