CREATE OR REPLACE FUNCTION obter_tiss_param_convenio (cd_estabelecimento_p bigint, cd_convenio_p bigint, nm_campo_p text) RETURNS varchar AS $body$
DECLARE


ie_solic_dem_pagto_w	varchar(255);
ie_dt_emissao_guia_w	varchar(255);
ie_dt_autor_sadt_w	varchar(255);
ie_data_entrada_ri_w	varchar(255);
ie_ref_solic_dem_pagto_w	varchar(255);
ds_retorno_w		varchar(255) := '';


BEGIN

select	coalesce(max(ie_solic_dem_pagto),'N'),
	coalesce(max(ie_dt_emissao_guia),'P'),
	coalesce(max(ie_dt_autor_sadt),'A'),
	coalesce(max(ie_data_entrada_ri),'E'),
	coalesce(max(ie_ref_solic_dem_pagto),'P')
into STRICT	ie_solic_dem_pagto_w,
	ie_dt_emissao_guia_w,
	ie_dt_autor_sadt_w,
	ie_data_entrada_ri_w,
	ie_ref_solic_dem_pagto_w
from	tiss_parametros_convenio
where	cd_estabelecimento	= cd_estabelecimento_p
and	cd_convenio		= cd_convenio_p;

if (nm_campo_p = 'IE_SOLIC_DEM_PAGTO') then
	ds_retorno_w	:= ie_solic_dem_pagto_w;
elsif (nm_campo_p = 'IE_DT_EMISSAO_GUIA') then
	ds_retorno_w	:= ie_dt_emissao_guia_w;
elsif (nm_campo_p = 'IE_DT_AUTOR_SADT') then
	ds_retorno_w	:= ie_dt_autor_sadt_w;
elsif (nm_campo_p = 'IE_DATA_ENTRADA_RI') then
	ds_retorno_w	:= ie_data_entrada_ri_w;
elsif (nm_campo_p = 'IE_REF_SOLIC_DEM_PAGTO') then
	ds_retorno_w	:= ie_ref_solic_dem_pagto_w;
end if;

return	ds_retorno_w;

end;
$body$
