CREATE OR REPLACE FUNCTION obter_title_req_produto (NR_SEQ_PRODUCT_REQUIREMENT_P bigint) RETURNS varchar AS $body$
DECLARE

  WRK_RETURN varchar(265);

BEGIN
  BEGIN
    SELECT nr_sequencia || ' - ' || ds_title INTO STRICT WRK_RETURN
      FROM REG_PRODUCT_REQUIREMENT 
    WHERE nr_sequencia = NR_SEQ_PRODUCT_REQUIREMENT_P;
  EXCEPTION
    WHEN no_data_found THEN
      WRK_RETURN := '';
  END;

 RETURN WRK_RETURN;
END;
$body$
