CREATE OR REPLACE FUNCTION obter_titular_convenio (NR_ATENDIMENTO_P bigint, CD_CONVENIO_p bigint, IE_OPCAO_P text) RETURNS varchar AS $body$
DECLARE



/*	ie_valor_p		C - Código
				D - Descrição
*/
ds_resultado_w		varchar(60);
cd_pessoa_titular_w	varchar(10);


BEGIN

select	max(P.CD_PESSOA_TITULAR)
into STRICT	cd_pessoa_titular_w
from	PESSOA_TITULAR_CONVENIO P,
	ATENDIMENTO_PACIENTE A
where	P.CD_PESSOA_FISICA = A.CD_PESSOA_FISICA
and	A.NR_ATENDIMENTO = NR_ATENDIMENTO_P
and	P.CD_CONVENIO = CD_CONVENIO_P;

if (upper(IE_OPCAO_P) = 'C') then
	ds_resultado_w := cd_pessoa_titular_w;
else
	if (cd_pessoa_titular_w IS NOT NULL AND cd_pessoa_titular_w::text <> '') then
		ds_resultado_w := substr(OBTER_NOME_PF(cd_pessoa_titular_w),1,60);
	end if;
end if;

RETURN	ds_resultado_w;

END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
