SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_titulos_baixa ( nr_titulo_pagar_p bigint, nr_titulo_receber_p bigint, nr_seq_baixa_p bigint) RETURNS varchar AS $body$
DECLARE


nr_titulo_baixa_w		bigint;
ds_lista_titulos_w		varchar(4000);


BEGIN

if (nr_titulo_pagar_p IS NOT NULL AND nr_titulo_pagar_p::text <> '') then

	select	a.nr_tit_receber
	into STRICT	nr_titulo_baixa_w
	from	titulo_pagar_baixa a
	where	a.nr_titulo	= nr_titulo_pagar_p
	and	a.nr_sequencia	= nr_seq_baixa_p;

	select	substr(obter_select_concatenado_bv(	'select distinct nr_titulo from titulo_receber_liq where nr_tit_pagar = :nr_tit_pagar ' ||
						'and nr_seq_baixa_pagar = :nr_seq_baixa',
						'nr_tit_pagar=' || nr_titulo_pagar_p || ';nr_seq_baixa=' || nr_seq_baixa_p, ', '),1,4000)
	into STRICT	ds_lista_titulos_w
	;

elsif (nr_titulo_receber_p IS NOT NULL AND nr_titulo_receber_p::text <> '') then

	select	a.nr_tit_pagar
	into STRICT	nr_titulo_baixa_w
	from	titulo_receber_liq a
	where	a.nr_titulo	= nr_titulo_receber_p
	and	a.nr_sequencia	= nr_seq_baixa_p;

	select	substr(obter_select_concatenado_bv(	'select distinct nr_titulo from titulo_pagar_baixa where nr_tit_receber = :nr_tit_rec ' ||
						'and nr_seq_baixa_rec = :nr_seq_baixa',
						'nr_tit_rec=' || nr_titulo_receber_p || ';nr_seq_baixa=' || nr_seq_baixa_p, ', '),1,4000)
	into STRICT	ds_lista_titulos_w
	;

end if;

/*LVSANTOS OS 1724202 24/07/2018 - Esta duplicando os títulos, pois pega os títulos de duas formas nos selects acima. Fiz essa tratativa para caso já possuir, não duplicar*/

if ( coalesce(obter_se_contido_char(ds_lista_titulos_w, nr_titulo_baixa_w),'N') = 'S') then
	nr_titulo_baixa_w := null;
end if;

if (nr_titulo_baixa_w IS NOT NULL AND nr_titulo_baixa_w::text <> '') then
	if (ds_lista_titulos_w IS NOT NULL AND ds_lista_titulos_w::text <> '') then
		ds_lista_titulos_w	:= nr_titulo_baixa_w || ', ' || ds_lista_titulos_w;
	else
		ds_lista_titulos_w	:= nr_titulo_baixa_w;
	end if;
end if;

return ds_lista_titulos_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_titulos_baixa ( nr_titulo_pagar_p bigint, nr_titulo_receber_p bigint, nr_seq_baixa_p bigint) FROM PUBLIC;

