CREATE OR REPLACE FUNCTION obter_titulos_cheque_cp ( nr_seq_cheque_p bigint) RETURNS varchar AS $body$
DECLARE


nr_titulo_w	titulo_pagar.nr_titulo%type;
ds_retorno_w	varchar(255);

c01 CURSOR FOR
SELECT	y.nr_titulo
from	titulo_pagar_baixa z,
	titulo_pagar y,
	cheque_bordero_titulo x
where	y.nr_titulo	= z.nr_titulo
and	x.nr_titulo	= y.nr_titulo
and	x.nr_seq_cheque	= nr_seq_cheque_p

union

select	z.nr_titulo
from	titulo_pagar_baixa w,
	titulo_pagar z,
	titulo_pagar_adiant y,
	adiantamento_pago_cheque x
where	z.nr_titulo		= w.nr_titulo
and	y.nr_titulo		= z.nr_titulo
and	x.nr_adiantamento	= y.nr_adiantamento
and	x.nr_seq_cheque		= nr_seq_cheque_p

union

select	y.nr_titulo
from	titulo_pagar_baixa z,
	titulo_pagar y,
	adiantamento_pago_cheque x
where	y.nr_titulo		= z.nr_titulo
and	x.nr_adiantamento	= y.nr_adiant_pago
and	x.nr_seq_cheque		= nr_seq_cheque_p;


BEGIN

open	c01;
loop
fetch	c01 into
	nr_titulo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	if (coalesce(ds_retorno_w::text, '') = '') or (length(ds_retorno_w)	< 240) then

		if (coalesce(ds_retorno_w::text, '') = '') then

			ds_retorno_w	:= nr_titulo_w;

		else

			ds_retorno_w	:= ds_retorno_w || ', ' || nr_titulo_w;

		end if;

	end if;

end	loop;
close	c01;

RETURN ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
