CREATE OR REPLACE FUNCTION obter_titulos_cheque_cr (nr_seq_cheque_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(2000)	:= null;
nr_titulo_w	bigint;

c01 CURSOR FOR
SELECT	distinct
	a.nr_seq_titulo_receber
from	movto_trans_financ a
where	(a.nr_seq_titulo_receber IS NOT NULL AND a.nr_seq_titulo_receber::text <> '')
and	exists (select	1
		from	movto_trans_financ x
		where	x.nr_seq_caixa	= a.nr_seq_caixa
		and	x.nr_seq_lote	= a.nr_seq_lote
		and	x.nr_seq_cheque	= nr_seq_cheque_p);


BEGIN

if (nr_seq_cheque_p IS NOT NULL AND nr_seq_cheque_p::text <> '') then
	open c01;
	loop
	fetch c01 into
		nr_titulo_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		if (coalesce(ds_retorno_w::text, '') = '') then
			ds_retorno_w	:= ds_retorno_w || nr_titulo_w;
		else
			ds_retorno_w	:= ds_retorno_w || ', ' || nr_titulo_w;
		end if;
	end loop;
	close c01;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_titulos_cheque_cr (nr_seq_cheque_p bigint) FROM PUBLIC;

