CREATE OR REPLACE FUNCTION obter_titulos_conta_prot (nr_seq_protocolo_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_todas_contas_w	varchar(1) := 'S';
nr_titulo_w		bigint;
nr_interno_conta_w	bigint;

C01 CURSOR FOR 
	SELECT	nr_interno_conta 
	from	conta_paciente 
	where	nr_seq_protocolo = nr_seq_protocolo_p;
				

BEGIN 
 
ie_todas_contas_w	:= 'S';
 
open C01;
loop 
fetch C01 into	 
	nr_interno_conta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	select	coalesce(max(nr_titulo),0) 
	into STRICT	nr_titulo_w 
	from	titulo_receber 
	where	nr_interno_conta = nr_interno_conta_w;
	 
	if (nr_titulo_w = 0) then 
		ie_todas_contas_w	:= 'N';
		exit;
	end if;
	 
	 
	end;
end loop;
close C01;
 
return ie_todas_contas_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
