CREATE OR REPLACE FUNCTION obter_titulos_convenio_receb (nr_seq_receb_p bigint) RETURNS varchar AS $body$
DECLARE

 
nr_titulo_w	bigint;
ds_titulos_w	varchar(4000);

c01 CURSOR FOR 
SELECT	c.nr_titulo 
from	titulo_receber c, 
	conta_paciente b,	 
	convenio_retorno_item a, 
	convenio_ret_receb d 
where	a.nr_interno_conta	= b.nr_interno_conta 
and	b.nr_interno_conta	= c.nr_interno_conta 
and	a.nr_seq_retorno	= d.nr_seq_retorno 
and	d.nr_seq_receb		= nr_seq_receb_p 

union
 
SELECT	c.nr_titulo 
from	titulo_receber c, 
	conta_paciente b,	 
	convenio_retorno_item a, 
	convenio_ret_receb d 
where	a.nr_interno_conta	= b.nr_interno_conta 
and	b.nr_seq_protocolo	= c.nr_seq_protocolo 
and	a.nr_seq_retorno	= d.nr_seq_retorno 
and	d.nr_seq_receb		= nr_seq_receb_p 
order	by 1;


BEGIN 
 
ds_titulos_w	:= '';
 
open c01;
loop 
fetch c01 into 
	nr_titulo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	if (ds_titulos_w IS NOT NULL AND ds_titulos_w::text <> '') then 
		ds_titulos_w	:= substr(ds_titulos_w || ', ' || nr_titulo_w, 1, 255);
	else 
		ds_titulos_w	:= nr_titulo_w;
	end if;
end loop;
close c01;
 
return	ds_titulos_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_titulos_convenio_receb (nr_seq_receb_p bigint) FROM PUBLIC;

