SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_titulos_gp_js ( ie_formato_p text, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(4000) := '';
nm_coluna_w	varchar(15) := '';
dt_horario_w	varchar(20) := '';
ds_sql_w	varchar(200) := '';
nm_usuario_w	varchar(20) := '';

c01 CURSOR FOR
SELECT   t.column_name
from     user_tab_columns t
where    t.table_name = 'W_ADEP'
and      substr(t.column_name,1,4) = 'HORA'
and      exists (select 1 from w_adep where nm_usuario = nm_usuario_p)
and      obter_se_valor_coluna_adep(t.column_name, nm_usuario_p) = 'S'
order by
         somente_numero(t.column_name);


BEGIN

open C01;
loop
fetch C01 into
	nm_coluna_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	dt_horario_w := '';

	if (ie_formato_p = 'HM') then
		ds_sql_w :=  	'select substr(' || nm_coluna_w || ',12,16) from w_adep where ie_tipo_item = ' || chr(39) || 'H' || chr(39) || ' and nm_usuario = :nm_usuario';
	else
		ds_sql_w :=  	'select substr(' || nm_coluna_w || ',1,16) from w_adep where ie_tipo_item = ' || chr(39) || 'H' || chr(39) || ' and nm_usuario = :nm_usuario';
	end if;

	dt_horario_w := Obter_valor_Dinamico_char_bv(ds_sql_w, 'nm_usuario=' || nm_usuario_p || ';', dt_horario_w);

	ds_retorno_w := ds_retorno_w || dt_horario_w || ';';

	end;
end loop;
close C01;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
