CREATE OR REPLACE FUNCTION obter_titulos_movto_cartao_cr ( nr_seq_movto_cartao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(2000);
nr_seq_trans_financ_w	bigint;
nr_seq_lote_w		bigint;
nr_titulo_w		bigint;
nr_seq_caixa_w		bigint;

C01 CURSOR FOR
	SELECT	nr_seq_titulo_receber
	from	movto_trans_financ b
	where	b.nr_seq_caixa		= nr_seq_caixa_w
	and	b.nr_seq_lote		= nr_seq_lote_w
	and	(nr_seq_titulo_receber IS NOT NULL AND nr_seq_titulo_receber::text <> '');


BEGIN

ds_retorno_w	:= null;

if (nr_seq_movto_cartao_p IS NOT NULL AND nr_seq_movto_cartao_p::text <> '') then

	select	max(nr_seq_caixa)
	into STRICT	nr_seq_caixa_w
	from	movto_trans_financ a
	where	nr_seq_movto_cartao	= nr_seq_movto_cartao_p
	and	(nr_seq_caixa IS NOT NULL AND nr_seq_caixa::text <> '');

	select	max(a.nr_seq_lote)
	into STRICT	nr_seq_lote_w
	from	movto_trans_financ a
	where	a.nr_seq_caixa		= nr_seq_caixa_w
	and	a.nr_seq_movto_cartao	= nr_seq_movto_cartao_p;

	open C01;
	loop
	fetch C01 into
		nr_titulo_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		ds_retorno_w	:= ds_retorno_w	|| nr_titulo_w || ', ';
	end loop;
	close C01;

	ds_retorno_w	:= substr(ds_retorno_w,1,(length(ds_retorno_w)-2));

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
