CREATE OR REPLACE FUNCTION obter_titulos_nf ( nr_seq_nota_p bigint, ds_separador_p text) RETURNS varchar AS $body$
DECLARE


nr_titulo_w			varchar(10);
ds_retorno_w			varchar(255);

c01 CURSOR FOR
SELECT	nr_titulo
from	titulo_receber
where	nr_seq_nf_saida = nr_seq_nota_p
order by 1;


BEGIN

open c01;
loop
fetch c01 into
	nr_titulo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	ds_retorno_w := substr(ds_retorno_w || nr_titulo_w || ds_separador_p,1,255);
end loop;
close c01;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
