CREATE OR REPLACE FUNCTION obter_titulos_pagar_nf ( nr_seq_nota_p bigint, ds_separador_p text) RETURNS varchar AS $body$
DECLARE


nr_titulo_pagar_w			bigint;
ds_titulo_pagar_w			varchar(400);

c01 CURSOR FOR
SELECT	nr_titulo
from	titulo_pagar
where	nr_seq_nota_fiscal = nr_seq_nota_p;


BEGIN

open c01;
loop
fetch c01 into
	nr_titulo_pagar_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	ds_titulo_pagar_w	:= substr(ds_titulo_pagar_w || nr_titulo_pagar_w	 || ds_separador_p,1,400);
end loop;
close c01;

return ds_titulo_pagar_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
