SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_titulos_protocolo (NR_SEQ_PROTOCOLO_P bigint) RETURNS varchar AS $body$
DECLARE
	
 
nr_titulo_w		bigint;
ds_titulo_w		varchar(2000) := '';

C01 CURSOR FOR 
	SELECT 	/*+ index (b CONPACI_PROCONV_FK_I) */ 
		nr_titulo 
	from titulo_receber a, 
		conta_paciente b 
	where (nr_seq_protocolo_p <> 0) 
	 and (a.nr_interno_conta = b.nr_interno_conta) 
	 and (b.nr_seq_protocolo = nr_seq_protocolo_p) 
	order by 1;


BEGIN 
 
open C01;
loop 
fetch C01 into nr_titulo_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	if (length(coalesce(ds_titulo_w, '0')) < 1980) then 
		ds_titulo_w := ds_titulo_w || nr_titulo_w ||', ';
	end if;
end loop;
close C01;
 
if (length(ds_titulo_w) > 2) then 
	ds_titulo_w	:= substr(ds_titulo_w,1,length(ds_titulo_w) - 2);
end if;
 
RETURN ds_titulo_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
