CREATE OR REPLACE FUNCTION obter_titulo_academico_pf (cd_pessoa_fisica_p text, ie_sigla_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	pf_forma_tratamento.ds_forma_tratamento%type;


BEGIN
	
	select 	max(CASE WHEN ie_sigla_p='S' THEN  a.ds_sigla  ELSE a.ds_forma_tratamento END )
	into STRICT	ds_retorno_w
	from 	pf_forma_tratamento a,
			pessoa_fisica b
	where   b.cd_pessoa_fisica = cd_pessoa_fisica_p
	and     a.nr_sequencia = b.nr_seq_forma_trat
	and	coalesce(a.ie_situacao, 'A') = 'A';

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
