CREATE OR REPLACE FUNCTION obter_titulo_atend_pendente (nr_atendimento_p bigint, nr_interno_conta_p bigint) RETURNS varchar AS $body$
DECLARE

 
nr_titulo_retorno_w	varchar(255) := null;
ie_tipo_convenio_w	smallint;
nr_titulo_w		bigint;

c01 CURSOR FOR 
	SELECT	b.nr_titulo nr_titulo 
	from	titulo_receber b 
	where	b.nr_interno_conta = nr_interno_conta_p 
	and	ie_tipo_convenio_w = 1 
	and	b.ie_situacao 	= '1' 
	and	b.dt_vencimento < clock_timestamp();


BEGIN 
 
select	a.ie_tipo_convenio 
into STRICT	ie_tipo_convenio_w 
from	atendimento_paciente a 
where	a.nr_atendimento = nr_atendimento_p;
 
open	c01;
loop 
fetch	c01 into nr_titulo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	 
	nr_titulo_retorno_w	:= nr_titulo_retorno_w || nr_titulo_w || ',';
 
	end;	
end loop;
close c01;
 
RETURN	nr_titulo_retorno_w;
 
END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_titulo_atend_pendente (nr_atendimento_p bigint, nr_interno_conta_p bigint) FROM PUBLIC;

