CREATE OR REPLACE FUNCTION obter_titulo_bordero (nr_bordero_p bigint) RETURNS varchar AS $body$
DECLARE


nr_titulo_w		varchar(20);
ds_titulos_w		varchar(254);

c01 CURSOR FOR
	SELECT	CASE WHEN coalesce(nr_titulo::text, '') = '' THEN ''  ELSE nr_titulo || ', ' END
	from	CHEQUE_BORDERO_TITULO
	WHERE	nr_bordero = nr_bordero_p;


BEGIN

open c01;
loop
fetch c01 into
	nr_titulo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	ds_titulos_w := substr(ds_titulos_w || nr_titulo_w,1,254);
end loop;
close c01;

return	ds_titulos_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
