SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_titulo_conta_guia ( nr_interno_conta_p bigint, cd_autorizacao_p text, nr_seq_retorno_p bigint, nr_seq_protocolo_p bigint) RETURNS bigint AS $body$
DECLARE


nr_titulo_w		bigint;
cd_convenio_w		bigint;
cd_estabelecimento_w	smallint;
ie_tit_ret_senha_w	varchar(1);
nr_seq_protocolo_w	bigint;
nr_titulo_conta_w	bigint;
nr_titulo_guia_w	bigint;
nr_titulo_senha_w	bigint;
nr_titulo_protocolo_w	bigint;
nr_seq_lote_prot_w	bigint;


BEGIN

if (coalesce(nr_seq_protocolo_p::text, '') = '') then
	select	max(a.nr_seq_protocolo)
	into STRICT	nr_seq_protocolo_w
	from	conta_paciente a
	where	a.nr_interno_conta	= nr_interno_conta_p;
else
	nr_seq_protocolo_w	:= nr_seq_protocolo_p;
end if;

if (coalesce(nr_seq_retorno_p::text, '') = '') then

	if (coalesce(nr_seq_protocolo_w::text, '') = '') then

		select	max(a.cd_convenio_parametro),
			max(a.cd_estabelecimento)
		into STRICT	cd_convenio_w,
			cd_estabelecimento_w
		from	conta_paciente a
		where	a.nr_interno_conta	= nr_interno_conta_p;

	else

		select	max(a.cd_convenio),
			max(a.cd_estabelecimento)
		into STRICT	cd_convenio_w,
			cd_estabelecimento_w
		from	protocolo_convenio a
		where	a.nr_seq_protocolo	= nr_seq_protocolo_w;

	end if;

else

	select	max(a.cd_convenio),
		max(a.cd_estabelecimento)
	into STRICT	cd_convenio_w,
		cd_estabelecimento_w
	from	convenio_retorno a
	where	a.nr_sequencia		= nr_seq_retorno_p;

end if;

select	coalesce(max(ie_tit_ret_senha),'N')
into STRICT	ie_tit_ret_senha_w
from	convenio_estabelecimento
where	cd_estabelecimento	= cd_estabelecimento_w
and	cd_convenio		= cd_convenio_w;

if (nr_interno_conta_p IS NOT NULL AND nr_interno_conta_p::text <> '') then

	select	max(a.nr_titulo)
	into STRICT	nr_titulo_conta_w
	from	titulo_receber a
	where	a.ie_situacao		not in ('3','5')
	and	coalesce(a.nr_documento,nr_interno_conta_p)	= nr_interno_conta_p
	and	a.nr_interno_conta	= nr_interno_conta_p;

	if (cd_autorizacao_p IS NOT NULL AND cd_autorizacao_p::text <> '') then

		select	max(a.nr_titulo)
		into STRICT	nr_titulo_guia_w
		from	titulo_receber a
		where	a.ie_situacao		not in ('3','5')
		and	to_char(a.nr_documento)	= cd_autorizacao_p
		and	a.nr_interno_conta	= nr_interno_conta_p;

		if (ie_tit_ret_senha_w = 'S') then

			select	max(a.nr_titulo)
			into STRICT	nr_titulo_senha_w
			from	conta_paciente b,
				titulo_receber a
			where	substr(obter_senha_atendimento(b.nr_atendimento),1,20)	= cd_autorizacao_p
			and	a.nr_interno_conta	= b.nr_interno_conta
			and	a.ie_situacao		not in ('3','5')
			and	a.nr_documento		<> nr_interno_conta_p
			and	to_char(a.nr_documento)	<> cd_autorizacao_p
			and	a.nr_interno_conta	= nr_interno_conta_p;

		end if;

	end if;

end if;

if (nr_seq_protocolo_w IS NOT NULL AND nr_seq_protocolo_w::text <> '') then

	select	max(nr_seq_lote_protocolo)
	into STRICT	nr_seq_lote_prot_w
	from	protocolo_convenio
	where	nr_seq_protocolo	= nr_seq_protocolo_w;

	if (nr_seq_lote_prot_w IS NOT NULL AND nr_seq_lote_prot_w::text <> '') then

		select	max(a.nr_titulo)
		into STRICT	nr_titulo_protocolo_w
		from	titulo_receber a
		where	a.ie_situacao		not in ('3','5')
		and	coalesce(a.nr_interno_conta::text, '') = ''
		and	a.nr_seq_lote_prot	= nr_seq_lote_prot_w;
	end if;

	if (coalesce(nr_titulo_protocolo_w::text, '') = '') then

		select	max(a.nr_titulo)
		into STRICT	nr_titulo_protocolo_w
		from	titulo_receber a
		where	a.ie_situacao		not in ('3','5')
		and	coalesce(a.nr_interno_conta::text, '') = ''
		and	a.nr_seq_protocolo	= nr_seq_protocolo_w;
	end if;

end if;

select	max(a.nr_titulo)
into STRICT	nr_titulo_w
from	titulo_receber a
where	a.nr_titulo	in (nr_titulo_conta_w,nr_titulo_guia_w,nr_titulo_senha_w,nr_titulo_protocolo_w);

if (coalesce(nr_titulo_w::text, '') = '') and (ie_tit_ret_senha_w <> 'S') then /* ahoffelder - OS 259784 - 27/10/2010 */
	select	max(a.nr_titulo)
	into STRICT	nr_titulo_w
	from	titulo_receber a
	where	a.ie_situacao		not in ('3','5')
	and	a.nr_interno_conta	= nr_interno_conta_p;

end if;

return	nr_titulo_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_titulo_conta_guia ( nr_interno_conta_p bigint, cd_autorizacao_p text, nr_seq_retorno_p bigint, nr_seq_protocolo_p bigint) FROM PUBLIC;

