SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_titulo_conta_prot_lote ( NR_SEQ_PROTOCOLO_P bigint, NR_INTERNO_CONTA_P bigint) RETURNS varchar AS $body$
DECLARE

 
nr_titulo_w			bigint;
ds_titulo_w			varchar(2000) := '';
nr_seq_lote_protocolo_w  	titulo_receber.nr_seq_lote_prot%type;

 
C01 CURSOR FOR 
	SELECT /*+ index (a titrece_proconv_fk_i) */ 
		nr_titulo 
	from 	titulo_receber a 
	where (nr_seq_protocolo_p <> 0) 
	and (nr_seq_protocolo = nr_seq_protocolo_p) 
	and 	coalesce(a.ie_situacao,'0') not in ('3','5') 
	
union
 
	SELECT /*+ index (a titrece_conpaci_fk_i) */ 
		nr_titulo 
	from 	titulo_receber a 
	where (nr_interno_conta_p <> 0) 
	and (nr_interno_conta = nr_interno_conta_p) 
	and 	coalesce(a.ie_situacao,'0') not in ('5') 
	
union
 
	select 	a.nr_titulo 
	from 	titulo_receber a 
	where (a.nr_seq_lote_prot <> 0) 
	and (a.nr_seq_lote_prot = nr_seq_lote_protocolo_w) 
	and 	coalesce(a.ie_situacao,'0') not in ('5');
	
	--Retirada a opção 3 deste not in pois se incluirmos os títulos cancelados aqui,,	  --em contas que estejam com a situação CANCELADA, o título não será exibido 
 
 
--	union				Edgar 13/08/2005, OS 22084, está causando problemas para o cliente 
--	select /*+ index (a titrece_conpaci_fk_i) */   /*Anderson e Elemar - 29/07/2005 OS 21219 Inclui o union*/ 
/*		nr_titulo 
	from titulo_receber a, 
		conta_paciente b 
	where (nr_seq_protocolo_p <> 0) 
	 and (a.nr_interno_conta = b.nr_interno_conta) 
	 and (b.nr_seq_protocolo = nr_seq_protocolo_p) 
 
	order by 1; 
*/
 
 

BEGIN 
 
select 	coalesce(max(nr_seq_lote_protocolo),0) 
into STRICT 	nr_seq_lote_protocolo_w 
from 	protocolo_convenio 
where	nr_seq_protocolo = nr_seq_protocolo_p;
 
open C01;
 
loop 
fetch C01 into nr_titulo_w;
 
	EXIT WHEN NOT FOUND; /* apply on C01 */
	if (length(coalesce(ds_titulo_w, '0')) < 1980) then 
		ds_titulo_w := ds_titulo_w || nr_titulo_w ||',';
 
 
	end if;
end loop;
close C01;
 
if (length(ds_titulo_w) > 2) then 
	ds_titulo_w	:= substr(ds_titulo_w,1,length(ds_titulo_w) - 1);
 
end if;
 
RETURN ds_titulo_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
