CREATE OR REPLACE FUNCTION obter_titulo_cotacao ( nr_cot_compra_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_tipo_titulo_w						varchar(15);
ds_titulo_w						varchar(255);


BEGIN

select (max(Obter_Valor_Param_Usuario(915, 108, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p)))
into STRICT	ie_tipo_titulo_w
;

if (ie_tipo_titulo_w = '0') then
	ds_titulo_w := wheb_mensagem_pck.get_Texto(311490, 'NR_COT_COMPRA_P='|| NR_COT_COMPRA_P); /*'Cotacao Nr: ' || nr_cot_compra_p;*/
elsif (ie_tipo_titulo_w = '1') then
	ds_titulo_w := wheb_mensagem_pck.get_Texto(311491, 'NR_COT_COMPRA_P='|| NR_COT_COMPRA_P); /*'Cotação Nr: ' || nr_cot_compra_p;*/
end if;


return	ds_titulo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
