SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_titulo_ger_senha (nm_funcao_p text, nm_computador_p text, cd_estabelecimento_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_local_curto_w varchar(80);
ds_estrategia_atual_w varchar(255);

nr_seq_local_atual_w bigint;
ds_titulo_w varchar(255);
ds_status_usuario_logado_w varchar(255);

BEGIN

ds_titulo_w:= nm_funcao_p;


select	coalesce(max(a.nr_sequencia), 0)
into STRICT	nr_seq_local_atual_w
from	maquina_local_senha a,
	computador b
where    a.nr_seq_computador	= b.nr_sequencia
and      a.cd_estabelecimento   = cd_estabelecimento_p
and	 nm_computador_pesquisa = padronizar_nome(upper(nm_computador_p))
and	coalesce(a.ie_situacao, 'A') = 'A';


SELECT	MAX(a.ds_local_curto)
INTO STRICT	ds_local_curto_w
FROM	maquina_local_senha a,
	computador b
WHERE	a.nr_seq_computador	= b.nr_sequencia
AND	nm_computador_pesquisa 	= padronizar_nome(UPPER(nm_computador_p))
and	coalesce(a.ie_situacao, 'A') = 'A';

ds_titulo_w:= ds_titulo_w || ' - ' || obter_desc_expressao(313779)|| ' '|| ds_local_curto_w;

select	max(a.ds_estrategia)
into STRICT	ds_estrategia_atual_w
from	ESTRATEGIA_CHAMADA_SENHA a,
	maquina_local_senha b
where	b.nr_sequencia = nr_seq_local_atual_w
and	b.NR_SEQ_ESTRATEGIA_SENHA  = a.nr_sequencia;

if (ds_estrategia_atual_w IS NOT NULL AND ds_estrategia_atual_w::text <> '') then
	ds_titulo_w:= ds_titulo_w || ' - ' || obter_desc_expressao(289606) ||' '|| ds_estrategia_atual_w;
end if;

ds_status_usuario_logado_w:= substr(obter_usuario_status_senha(obter_seq_usuario_status_senha(nm_usuario_p)),1,255);

if (ds_status_usuario_logado_w IS NOT NULL AND ds_status_usuario_logado_w::text <> '') then
	ds_titulo_w:= ds_titulo_w || ' - ' || obter_desc_expressao(298824) ||': '|| ds_status_usuario_logado_w;
end if;

return	ds_titulo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
