SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_titulo_lote_caixa ( nr_seq_movto_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
TP	= Título a Pagar
TR	= Título a Receber
*/
nr_titulo_result_w		bigint;
nr_titulo_receber_w	bigint;
nr_titulo_pagar_w		bigint;
nr_seq_lote_w		bigint;
nr_seq_caixa_w		bigint;


BEGIN

if (nr_seq_movto_p IS NOT NULL AND nr_seq_movto_p::text <> '') and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') then

	select	a.nr_seq_lote,
		a.nr_seq_caixa
	into STRICT	nr_seq_lote_w,
		nr_seq_caixa_w
	from	movto_trans_financ a
	where	a.nr_sequencia	= nr_seq_movto_p;


	select	max(a.nr_seq_titulo_pagar),
		max(a.nr_seq_titulo_receber)
	into STRICT	nr_titulo_pagar_w,
		nr_titulo_receber_w
	from	movto_trans_financ a
	where	a.nr_seq_caixa	= nr_seq_caixa_w
	and	a.nr_seq_lote	= nr_seq_lote_w;

end if;

if (ie_opcao_p = 'TP') then
	nr_titulo_result_w := nr_titulo_pagar_w;
elsif (ie_opcao_p = 'TR') then
	nr_titulo_result_w := nr_titulo_receber_w;
end if;

return	nr_titulo_result_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
