SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_titulo_lote_guia ( nr_interno_conta_p bigint, cd_autorizacao_p text, nr_seq_lote_hist_p bigint) RETURNS bigint AS $body$
DECLARE

 
nr_titulo_w		bigint;
nr_seq_retorno_w	bigint;


BEGIN 
 
select	max(a.nr_seq_retorno) 
into STRICT	nr_seq_retorno_w 
from	lote_audit_hist_guia a 
where	a.nr_seq_lote_hist		= nr_seq_lote_hist_p 
and	a.nr_interno_conta		= nr_interno_conta_p 
and	coalesce(a.cd_autorizacao,'0')	= coalesce(coalesce(cd_autorizacao_p,a.cd_autorizacao),'0');
 
select	max(a.nr_titulo) 
into STRICT	nr_titulo_w 
from	convenio_retorno_item a 
where	a.nr_interno_conta		= nr_interno_conta_p 
and	a.cd_autorizacao		= coalesce(cd_autorizacao_p,a.cd_autorizacao) 
and	a.nr_seq_retorno		= nr_seq_retorno_w;
 
if (coalesce(nr_titulo_w::text, '') = '') then 
 
	select	max(b.nr_titulo) 
	into STRICT	nr_titulo_w 
	from	titulo_receber b, 
		conta_paciente_guia a 
	where	a.nr_interno_conta		= nr_interno_conta_p 
	and	a.cd_autorizacao		= coalesce(cd_autorizacao_p,a.cd_autorizacao) 
	and	a.nr_interno_conta		= b.nr_interno_conta 
	and	a.cd_autorizacao		= to_char(b.nr_documento);
 
	if (coalesce(nr_titulo_w::text, '') = '') then 
		select	max(c.nr_titulo) 
		into STRICT	nr_titulo_w 
		from	titulo_receber c, 
			conta_paciente b, 
			conta_paciente_guia a 
		where	a.nr_interno_conta		= nr_interno_conta_p 
		and	a.cd_autorizacao		= coalesce(cd_autorizacao_p,a.cd_autorizacao) 
		and	a.nr_interno_conta		= b.nr_interno_conta 
		and	b.nr_seq_protocolo		= c.nr_seq_protocolo;
	end if;
 
end if;
 
return	nr_titulo_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
