SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_titulo_lote_protocolo (nr_seq_lote_protocolo_p bigint) RETURNS varchar AS $body$
DECLARE

			 
nr_seq_protocolo_w	bigint;	
nr_titulo_w       bigint;
ds_titulo_w       varchar(2000) := '';

C01 CURSOR FOR 
	SELECT	a.nr_seq_protocolo 
	from 	protocolo_convenio a, 
		lote_protocolo b 
	where 	b.nr_sequencia	= a.nr_seq_lote_protocolo 
	and  	b.nr_sequencia	= nr_seq_lote_protocolo_p 
	order by a.nr_seq_protocolo;				
			 
C02 CURSOR FOR 
    SELECT /*+ index (a titrece_proconv_fk_i) */ 
		a.nr_titulo 
    from 	titulo_receber a 
    where	nr_seq_protocolo_w <> 0 
	and	coalesce(a.ie_situacao,'0') not in ('3', '5') 
	and 	a.nr_seq_protocolo = nr_seq_protocolo_w;
	
c03 CURSOR FOR 
	SELECT	nr_titulo 
	from	titulo_receber 
	where	nr_seq_lote_prot	= nr_seq_lote_protocolo_p 
	and	coalesce(ie_situacao,'0') not in ('3', '5');
	

BEGIN 
 
/*Primeiro busca os t√≠tulos do lote de protocolo*/
 
open C03;
loop 
fetch C03 into	 
	nr_titulo_w;
EXIT WHEN NOT FOUND; /* apply on C03 */
	begin 
	 
	if (length(coalesce(ds_titulo_w, '0')) < 1980) then 
		ds_titulo_w := ds_titulo_w || nr_titulo_w ||', ';
	end if;
	 
	end;
end loop;
close C03;
 
if (coalesce(ds_titulo_w::text, '') = '') then 
 
	open C01;
	loop 
	fetch C01 into	 
		nr_seq_protocolo_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		 
		open C02;
		loop 
		fetch C02 into	 
			nr_titulo_w;
		EXIT WHEN NOT FOUND; /* apply on C02 */
			begin 
			 
			if (length(coalesce(ds_titulo_w, '0')) < 1980) then 
				ds_titulo_w := ds_titulo_w || nr_titulo_w ||', ';
			end if;
			 
			end;
		end loop;
		close C02;
		 
		end;
	end loop;
	close C01;
end if;
 
if (length(ds_titulo_w) > 2) then 
    ds_titulo_w   := substr(ds_titulo_w,1,length(ds_titulo_w) - 2);
end if;
 
return	ds_titulo_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
