CREATE OR REPLACE FUNCTION obter_titulo_origem_nc ( nr_seq_nota_credito_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_lista_titulo_w		varchar(4000);
nr_titulo_w			bigint;

c01 CURSOR FOR 
SELECT	(obter_titulo_conta_guia(b.nr_interno_conta,b.cd_autorizacao,null,null))::numeric  
from	lote_audit_hist_guia b, 
	nota_credito a 
where	a.nr_sequencia			= nr_seq_nota_credito_p 
and	a.nr_seq_lote_audit_hist	= b.nr_seq_lote_hist 
and	coalesce(b.vl_saldo_guia,0)		= 0 
and	exists (SELECT	1 
	from	lote_audit_hist_item x 
	where	x.nr_seq_guia	= b.nr_sequencia 
	and	x.vl_glosa	> 0);


BEGIN 
 
open c01;
loop 
fetch c01 into 
	nr_titulo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
 
	if (ds_lista_titulo_w IS NOT NULL AND ds_lista_titulo_w::text <> '') and (length(ds_lista_titulo_w) < 3988) then 
		ds_lista_titulo_w := ds_lista_titulo_w || ', ' || nr_titulo_w;
	else 
		ds_lista_titulo_w := nr_titulo_w;
	end if;
 
end loop;
close c01;
 
RETURN ds_lista_titulo_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_titulo_origem_nc ( nr_seq_nota_credito_p bigint) FROM PUBLIC;

