SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_titulo_orig_cr ( nr_seq_nota_credito_p bigint) RETURNS varchar AS $body$
DECLARE

 
nr_titulo_w		bigint;
ds_titulo_w		varchar(255)	:= '';
ie_tamanho_w		bigint	:= 0;
ie_cursor_w		bigint;
			
 
c01 CURSOR FOR 
SELECT	a.nr_titulo 
from	titulo_receber a, 
	titulo_receber_nc b 
where	b.nr_seq_nota_credito		= nr_seq_nota_credito_p 
and	b.nr_titulo_rec			= a.nr_titulo 

union
 
SELECT	a.nr_titulo 
from	titulo_receber a, 
	lote_audit_hist_guia b, 
	nota_credito c 
where	c.nr_sequencia			= nr_seq_nota_credito_p 
and	c.nr_seq_lote_audit_hist		= b.nr_seq_lote_hist 
and	coalesce(b.vl_saldo_guia,0)		= 0 
and	exists (select	1 
	from	lote_audit_hist_item x 
	where	x.nr_seq_guia		= b.nr_sequencia 
	and	x.vl_glosa	> 0) 
and	(obter_titulo_conta_guia(b.nr_interno_conta,b.cd_autorizacao,null,null))::numeric  = a.nr_titulo 

union
 
select	a.nr_titulo 
from	titulo_receber a, 
	lote_audit_hist_guia b, 
	nota_credito c 
where	c.nr_sequencia			= nr_seq_nota_credito_p 
and	c.nr_seq_lote_hist_guia		= b.nr_sequencia 
and	(obter_titulo_conta_guia(b.nr_interno_conta,b.cd_autorizacao,null,null))::numeric  = a.nr_titulo;			
			 

BEGIN 
 
 
open C01;
loop 
fetch C01 into	 
	nr_titulo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	ie_tamanho_w	:= length(ds_titulo_w||'/'||nr_titulo_w);
	if (ie_tamanho_w <= 255) then 
	 
		if (coalesce(ds_titulo_w, 'X') <> 'X') then 
			 
			ds_titulo_w	:= substr(ds_titulo_w||'/'||nr_titulo_w, 1, 255);
			 
		else 
		 
			ds_titulo_w	:= nr_titulo_w; /*Primeira vez...*/
		end if;
		 
	end if;
	 
	end;
end loop;
close C01;
 
return	ds_titulo_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
