CREATE OR REPLACE FUNCTION obter_titulo_pagar_abatimento ( nr_titulo_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w	varchar(255);
nr_titulo_w	bigint;

c01 CURSOR FOR 
SELECT	distinct 
	nr_titulo 
from (SELECT	c.nr_titulo 
	from	titulo_receber_liq c 
	where	c.nr_tit_pagar	= nr_titulo_p 
	
union
 
	select	a.nr_tit_receber nr_titulo 
	from	titulo_pagar_baixa a 
	where	(a.nr_tit_receber IS NOT NULL AND a.nr_tit_receber::text <> '') 
	and	a.nr_titulo		= nr_titulo_p) alias1;


BEGIN 
 
open	c01;
loop 
fetch	c01 into 
	nr_titulo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
 
	if (coalesce(ds_retorno_w::text, '') = '') or (length(ds_retorno_w)	< 245) then 
 
		if (coalesce(ds_retorno_w::text, '') = '') then 
			ds_retorno_w	:= nr_titulo_w;
		else 
			ds_retorno_w	:= ds_retorno_w || ', ' || nr_titulo_w;
		end if;
 
	end if;
 
end	loop;
close	c01;
 
RETURN ds_retorno_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
