CREATE OR REPLACE FUNCTION obter_titulo_rec_cobr_inst ( nr_seq_tit_rec_cobr_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


cd_instrucao_w		varchar(5);


BEGIN

if (coalesce(nr_seq_tit_rec_cobr_p,0) > 0) then

	select 	coalesce(min(x.cd_instrucao),'00')
	into STRICT	cd_instrucao_w
	from	titulo_rec_cobr_instrucao x
	where	x.nr_seq_tit_rec_cobr = nr_seq_tit_rec_cobr_p;

end if;

if (ie_opcao_p = 'I') then
	return	cd_instrucao_w;
end if;

end;
