CREATE OR REPLACE FUNCTION obter_titulo_repasse_orig (nr_repasse_terceiro_p bigint) RETURNS varchar AS $body$
DECLARE


ds_lista_titulo_w	varchar(254) := '';
nr_titulo_w		bigint;

c01 CURSOR FOR
SELECT	nr_titulo
from	titulo_pagar
where	nr_repasse_terceiro	= nr_repasse_terceiro_p
and	ie_tipo_titulo		= 9

union

select	nr_titulo
from	repasse_terceiro_venc
where	nr_repasse_terceiro	= nr_repasse_terceiro_p
order	by nr_titulo;


BEGIN

open c01;
loop
fetch c01 into
	nr_titulo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	if (coalesce(ds_lista_titulo_w::text, '') = '') then
		ds_lista_titulo_w	:= nr_titulo_w;
	else
		ds_lista_titulo_w	:= substr(ds_lista_titulo_w || ', ' || nr_titulo_w,1,254);
	end if;

end loop;
close c01;

return ds_lista_titulo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
