CREATE OR REPLACE FUNCTION obter_titulo_resumo (nr_ordem_p bigint) RETURNS varchar AS $body$
DECLARE


ds_titulo_w		varchar(100);


BEGIN


if (nr_ordem_p = 1) then

	ds_titulo_w := '		* ' || wheb_mensagem_pck.get_texto(307016, null) || ' ' ||chr(10); -- Evoluções
elsif (nr_ordem_p = 2) then

	ds_titulo_w := '		* ' || wheb_mensagem_pck.get_texto(307017, null) || ' ' ||chr(10); -- Diagnósticos
elsif (nr_ordem_p = 3) then

	ds_titulo_w := '		* ' || wheb_mensagem_pck.get_texto(306711, 'DS_LISTA_PRESCRICAO_W=') || ' ' ||chr(10); -- Prescrições
elsif (nr_ordem_p = 4) then

	ds_titulo_w := '		* ' || wheb_mensagem_pck.get_texto(307211, null) || ' ' ||chr(10); -- Exames/Procedimentos/Cirurgias
elsif (nr_ordem_p = 5) then

	ds_titulo_w	:= '		* ' || wheb_mensagem_pck.get_texto(307212, null) || ' ' ||chr(10); -- Laudo Paciente
elsif (nr_ordem_p = 8) then

	ds_titulo_w	:= '		* ' || wheb_mensagem_pck.get_texto(331546, null) || ' ' ||chr(10); -- Precaução / Isolamento
elsif (nr_ordem_p = 9) then

	ds_titulo_w	:= '		* ' || wheb_mensagem_pck.get_texto(360705, null) || ' ' ||chr(10); -- Orientações de alta
end if;

return	ds_titulo_w;

end;
