CREATE OR REPLACE FUNCTION obter_tit_bordero_conta_fin (nr_bordero_p bigint) RETURNS varchar AS $body$
DECLARE

			 
nr_titulo_w 		titulo_pagar.nr_titulo%type;
lista_titulo_w		varchar(4000);		
-- Function criada para obter os titulos do border√¥ que nao possuem conta financeira informada. 
C01 CURSOR FOR 
	SELECT	nr_titulo 
	from	titulo_pagar_bordero_v a 
	where	exists (SELECT 1 
           from   titulo_pagar_classif x 
           where  coalesce(x.nr_seq_conta_financ::text, '') = '' 
           and   x.nr_titulo       = a.nr_titulo) 
	and   a.nr_bordero  = nr_bordero_p 
	
union
 
	select	nr_titulo 
	from	titulo_pagar_bordero_v a 
	where	not exists (	select	1 
				from	titulo_pagar_classif x 
				where	x.nr_titulo	= a.nr_titulo) 
	and	a.nr_bordero	= nr_bordero_p;	

BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_titulo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	if (coalesce(lista_titulo_w,'X') = 'X') then 
		lista_titulo_w	:= nr_titulo_w;
	elsif (length(lista_titulo_w) < 3980) then 
		lista_titulo_w	:= substr(lista_titulo_w || ', ' || nr_titulo_w,1,4000);
	end if;
	end;
end loop;
close C01;
 
return	lista_titulo_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_tit_bordero_conta_fin (nr_bordero_p bigint) FROM PUBLIC;

