SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_tit_cancel_pos_contab (nr_titulo_p bigint, dt_pos_contab_p timestamp) RETURNS varchar AS $body$
DECLARE

 
nr_sequencia_w		alteracao_valor.nr_sequencia%type;
dt_alteracao_w		timestamp;
ie_situacao_w		titulo_receber.ie_situacao%type;
ds_retorno_w		varchar(255) := 'N';
nr_seq_baixa_w		titulo_receber_liq.nr_sequencia%type;
dt_recebimento_w	titulo_receber_liq.dt_recebimento%type;
				
				 

BEGIN 
 
if ( coalesce(nr_titulo_p,0) <> 0 ) then 
 
  /*Buscar a ultima alteracao de valor do titulo de cancelamento*/
 
	select	max(a.nr_sequencia), 
		max(a.dt_alteracao) 
	into STRICT	nr_sequencia_w, 
		dt_alteracao_w 
	from	alteracao_valor a 
	where	a.nr_titulo = nr_titulo_p 
	and	a.cd_motivo = 3; 	
	 
	/*buscar situacao do titulo*/
 
	select	max(a.ie_situacao) 
	into STRICT	ie_situacao_w 
	from	titulo_receber a 
	where	a.nr_titulo	= nr_titulo_p;
	 
	/*Buscar ultima baixa do titulo*/
 
	select	max(a.nr_sequencia), 
		max(a.dt_recebimento) 
	into STRICT	nr_seq_baixa_w, 
		dt_recebimento_w 
	from	titulo_receber_liq a 
	where	a.nr_titulo	= nr_titulo_p;
	 
	if (ie_situacao_w = '3') then /*se a situacao do titulo for cancelado*/
 
	 
		if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then /*se a alteração de vencimento foi de cancelamento do titulo*/
 
	 
			if (dt_alteracao_w IS NOT NULL AND dt_alteracao_w::text <> '') then/*se tiver data de alteracao para o cancelamento*/
 
				if ( fim_dia(dt_alteracao_w) <= fim_dia(dt_pos_contab_p) ) then /*se a data de cancelamento for depois que a data de posicao contabil informada, o titulo deve ser apresentado no relatorio*/
 
					ds_retorno_w := 'S';
				else 
					ds_retorno_w := 'N';
				end if;
			end if;
		 
		elsif (nr_seq_baixa_w IS NOT NULL AND nr_seq_baixa_w::text <> '') then /*se foi uma baixa do titulo que cancelou*/
 
			 
			if (dt_recebimento_w IS NOT NULL AND dt_recebimento_w::text <> '') then 
				if ( fim_dia(dt_recebimento_w) <= fim_dia(dt_pos_contab_p) ) then /*se a data de baixa de cancelamento for depois que a data de posicao contabil informada, o titulo deve ser apresentado no relatorio*/
 
					ds_retorno_w := 'S';
				else 
					ds_retorno_w := 'N';
				end if;
			end if;
		 
		end if;
		 
	end if;
 
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_tit_cancel_pos_contab (nr_titulo_p bigint, dt_pos_contab_p timestamp) FROM PUBLIC;

