CREATE OR REPLACE FUNCTION obter_tit_escrit_conta_fin (nr_seq_escrit_p bigint) RETURNS varchar AS $body$
DECLARE


nr_titulo_w 		titulo_pagar.nr_titulo%type;
lista_titulos_w		varchar(4000);


C01 CURSOR FOR
	SELECT	nr_titulo
	from	titulo_pagar_escrit a
	where	not exists (SELECT	1
			from	titulo_pagar_classif x
			where	(x.nr_seq_conta_financ IS NOT NULL AND x.nr_seq_conta_financ::text <> '')
			and      x.nr_titulo             = a.nr_titulo)
	and	a.nr_seq_escrit		= nr_seq_escrit_p;

BEGIN

Open C01;
loop
fetch C01 into
	nr_titulo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (coalesce(coalesce(lista_titulos_w,null)::text, '') = '') then
		lista_titulos_w	:=	nr_titulo_w;
	elsif (length(lista_titulos_w) < 3980) then
		lista_titulos_w	:= substr(lista_titulos_w || ', ' || nr_titulo_w,1,4000);
	end if;
	end;
end loop;
close C01;

return lista_titulos_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
