CREATE OR REPLACE FUNCTION obter_tit_rec_controle_pes (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(2000) := '';
nr_titulo_w		bigint;

c01 CURSOR FOR
SELECT	nr_titulo
from	titulo_receber
where	nr_seq_controle_pessoa = nr_sequencia_p;


BEGIN

open c01;
loop
	fetch c01 into
		nr_titulo_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */

	ds_retorno_w := ds_retorno_w || nr_titulo_w || ',';

end loop;
close c01;

return substr(ds_retorno_w,1,length(ds_retorno_w)-1);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
