CREATE OR REPLACE FUNCTION obter_tit_rec_terceiro ( nr_seq_terceiro_p bigint) RETURNS varchar AS $body$
DECLARE


cd_pessoa_fisica_w	varchar(10);
cd_cgc_w		varchar(14);
nr_titulo_w		bigint;
ds_lista_titulo_w	varchar(4000);

C01 CURSOR FOR
SELECT	a.nr_titulo
from	titulo_receber a
where	a.ie_situacao	= '1'
and	((a.cd_pessoa_fisica = cd_pessoa_fisica_w) or (a.cd_cgc = cd_cgc_w));


BEGIN

select	max(a.cd_pessoa_fisica),
	max(a.cd_cgc)
into STRICT	cd_pessoa_fisica_w,
	cd_cgc_w
from	terceiro a
where	a.nr_sequencia = nr_seq_terceiro_p;

open C01;
loop
fetch C01 into
	nr_titulo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (coalesce(ds_lista_titulo_w,'X') = 'X') then
		ds_lista_titulo_w	:= nr_titulo_w;
	elsif (length(ds_lista_titulo_w) < 3980) then
		ds_lista_titulo_w	:= ds_lista_titulo_w || ', ' || nr_titulo_w;
	end if;
	end;
end loop;
close C01;

return	ds_lista_titulo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_tit_rec_terceiro ( nr_seq_terceiro_p bigint) FROM PUBLIC;

