SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_tit_rec_terc_conta (nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*	ie_opcao_p
'T'	- todos exceto cancelados
'C'	- somente cancelados
*/
ds_retorno_w	varchar(255);
nr_titulo_w	bigint;

c01 CURSOR FOR
SELECT	nr_titulo
from	titulo_receber
where	nr_seq_terc_conta	= nr_sequencia_p
and	(('T' = coalesce(ie_opcao_p,'T') and ie_situacao <> '3') or ('C' = ie_opcao_p and ie_situacao = '3'));


BEGIN

ds_retorno_w	:= '';

open c01;
loop
fetch c01 into
	nr_titulo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	ds_retorno_w	:= ds_retorno_w || to_char(nr_titulo_w) || ',';

end loop;
close c01;

return	substr(ds_retorno_w, 1, length(ds_retorno_w) - 1);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
