CREATE OR REPLACE FUNCTION obter_todos_cid_atendimento ( nr_atendimento_p bigint, ie_separador_p text, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


dt_diagnostico_w	timestamp;
cd_doenca_w		varchar(10);
cd_cids_w		varchar(4000);

C01 CURSOR FOR
	SELECT	cd_doenca
	FROM	diagnostico_doenca
	WHERE	nr_atendimento	= nr_atendimento_p
	ORDER BY dt_atualizacao;


BEGIN


OPEN C01;
	LOOP
	FETCH C01 INTO
		cd_doenca_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */

	cd_cids_w := cd_doenca_w || ie_separador_p || cd_cids_w;

	END LOOP;
CLOSE C01;

cd_cids_w := substr(cd_cids_w,1,(length(cd_cids_w)-1));

RETURN cd_cids_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
