SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_token_dasa ( cd_estabelecimento_p bigint, ie_tipo_atendimento_p bigint, ie_tipo_informacao_p text ) RETURNS varchar AS $body$
DECLARE

    ds_retorno_w varchar(4000) := NULL;


BEGIN
    if (ie_tipo_informacao_p = 'A') then
        SELECT max(ds_url_token||','||ds_client_id||','||ds_client_secret)
        into STRICT ds_retorno_w
        FROM lab_integracao_dasa
        WHERE cd_estabelecimento =cd_estabelecimento_p
            AND coalesce(ie_tipo_atendimento,'0') = coalesce(ie_tipo_atendimento_p,'0');
    END if;

    if (ie_tipo_informacao_p = 'P') then
        SELECT max(ds_url_pedido)
        into STRICT ds_retorno_w
        FROM lab_integracao_dasa
        WHERE cd_estabelecimento =cd_estabelecimento_p
        AND coalesce(ie_tipo_atendimento,'0') = coalesce(ie_tipo_atendimento_p,'0');
    end if;

    if (ie_tipo_informacao_p = 'R') then
        SELECT max(ds_url_resultado)
        into STRICT ds_retorno_w
        FROM lab_integracao_dasa
        WHERE cd_estabelecimento =cd_estabelecimento_p
        AND coalesce(ie_tipo_atendimento,'0') = coalesce(ie_tipo_atendimento_p,'0');
    end if;

    RETURN ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
